import { useState } from 'react';

const Example = () => {
  let val = useState('初期値');
  return (
    <>
      <input
        type="text"
        onChange={(e) => {
          val[0] = e.target.value;
        }}
      /> = {val[0]}
    </>
  );
};

export default Example;
