import { useState } from "react";

const Example = () => {
  const personObj = { name: "Tom", age: 18 };
  const [person, setPerson] = useState(personObj);

  return (
    <>
      <p>Name: </p>
      <p>Age: </p>
      <input
        onChange={personObj.name}
        value={personObj.name}
        type="text"
      />
      <input
        onChange={personObj.age}
        value={personObj.age}
        type="number"
      />
      <div><button>リセット</button></div>
    </>
  )
};

export default Example;
