import { useState } from 'react';

const Form = ({ addedTodo }) => {
  const [enteredTodo, setEnteredTodo] = useState('');

  // todoを追加する関数
  const addTodo = () => {
    // console.log(inpVal);

    const newTodo = {
      id: Math.random(),
      content: enteredTodo,
    };

    addedTodo(newTodo);
  };
  return (
    <>
      <form>
        <input
          type="text"
          value={enteredTodo}
          onChange={(e) => {
            setEnteredTodo(e.target.value);
          }}
        />
        <button onClick={addTodo}>追加</button>
      </form>
    </>
  );
};

export default Form;
