import { useState } from 'react';

const Form = () => {
  const [enteredTodo, setEnteredTodo] = useState('');

  // todoを追加する関数
  const addTodo = (e) => {
    const inpVal = e.target.value;

    const newTodo = {
      id: 1,
      content: inpVal,
    };
  };
  return (
    <>
      <input
        type="text"
        value={enteredTodo}
        onChange={(e) => {
          setEnteredTodo(e.target.value);
        }}
      />
      <button onClick={addTodo}>追加</button>
      {/* <span>{enteredTodo}</span> */}
    </>
  );
};

export default Form;
