import { useContext } from 'react';
import { ThemeContext } from '../context/ThemeContext';

const Main = () => {
  const [theme] = useContext(ThemeContext);
  return (
    <main className={`content-${theme}`}>
      <h1>テーマの切り替え</h1>
    </main>
  );
};

export default Main;
