    setfirst(first + 1);
  };
  const countUpSecond = () => {
    setsecond(second + 1);
  };
  const countDown = () => {
    setsecond(second - 1);
  };
  return (
    <>
      <div>{first}</div>
      <button onClick={countUpFirst}>+</button>
      <div>{second}</div>
      <button onClick={countUpSecond}>+</button>
      <button onClick={countDown}>-</button>
    </>
