import { useState } from 'react';

const Example = () => {
  const [first, setfirst] = useState(0);
  const [second, setsecond] = useState(0);

  const countUp = () => {
    setfirst((prev) => ++prev);
  };
  const countDown = () => {
    setsecond((prev) => --prev);
  };
  return (
    <>
      <div>{first}</div>
      <button onClick={countUp}>+</button>
      <div>{second}</div>
      <button onClick={countUp}>+</button>
      <button onClick={countDown}>-</button>
    </>
  );
};

export default Example;
